package week1.day2;

public class PrintEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("The even number are:");
		
		for (int i = 2; i <= 20 ; i++) {
			
			if (i % 2 == 0) {
				
				System.out.println(i);
				
			}
			
		}

	}

}
